import { Component, OnInit } from '@angular/core';
import { AngularFirestore } from '@angular/fire/compat/firestore';
import { AngularFireAuth } from '@angular/fire/compat/auth';
import { RecipeService } from '../../services/recipe.service';
import { InventoryService } from '../../services/inventory.service';

@Component({
  selector: 'app-favorites',
  templateUrl: './favorites.component.html',
  styleUrls: ['./favorites.component.scss'],
})
export class FavoritesComponent implements OnInit {
  userId: string | null = null;
  favorites: any[] = []; // Aquí se almacenan todas las recetas favoritas
  filteredFavorites: any[] = []; // Recetas filtradas por categoría
  isModalOpen = false;
  selectedRecipe: any | null = null;
  selectedCategory = 'desayuno'; // Categoría seleccionada por defecto
  isAuthenticated: boolean = false;
  availableIngredients: any[] = [];

  constructor(
    private auth: AngularFireAuth,
    private firestore: AngularFirestore,
    private recipeService: RecipeService, 
    private inventoryService: InventoryService,
  ) {
    console.log('en construct');
  }

  ngOnInit(): void {
    this.auth.authState.subscribe(user => {
      this.isAuthenticated = !!user;
      console.log('isAuthenticated subscribe :: ', this.isAuthenticated);
      if (this.isAuthenticated) {
        if (user && user.uid) {
          this.userId = user.uid;
          this.filterFavorites();
        }
      }
    });
  }

  // Método para filtrar las recetas según la categoría seleccionada
  filterFavorites() {
    console.log('this === ', this.userId);

    if (this.userId) {   
      this.filteredFavorites = [];
      const favoritos = this.firestore.collection('usuarios').doc(this.userId).collection('favoritos',
        ref => ref.where('category', '==', this.selectedCategory)
      );
      favoritos.get().forEach((querySnapshot) => {
        querySnapshot.forEach((doc) => {
          const datos = doc.data();
          console.log('datos ID :: ', doc.id);
          this.firestore.collection('recetas').doc(datos['recipeId']).get().forEach(
            (rec) => {
              const d: any = rec.data();
              d.id = doc.id;
              d.creadorId = datos['creadorId'];
              this.filteredFavorites.push(d);
            }
          );
        });
      });
    }
  }

  openRecipeModal(recipe: any) {
    this.selectedRecipe = recipe;
    this.checkIngredientsAvailability(recipe.ingredientes);
    this.recipeService.getRecipeCommentsWithUser(recipe.id).subscribe((comments: any[]) => {
      this.selectedRecipe.comentarios = comments;
    });
    this.isModalOpen = true;
  }

  // Método para cerrar el modal
  closeModal() {
    this.isModalOpen = false;
    this.selectedRecipe = null;
  }

  removeFromFavorites(recipeId: string) {
    console.log('recipeId :: ', recipeId);
    if (this.userId) {  
      this.firestore.collection('usuarios').doc(this.userId).collection('favoritos').doc(recipeId).delete();
      this.filterFavorites();
    }
  }

  checkIngredientsAvailability(recipeIngredients: { nombre: string; cantidad: number; unidad: string }[]) {
    if (this.userId) {
      this.inventoryService.getInventory(this.userId).subscribe(inventory => {
        this.availableIngredients = recipeIngredients.map(ingredient => {
          const inventoryItem = inventory.find(item => 
            item?.nombre?.toLowerCase() === ingredient?.nombre?.toLowerCase()
          );
          return {
            nombre: ingredient.nombre,
            cantidad: ingredient.cantidad,
            unidad: ingredient.unidad,
            disponible: inventoryItem ? inventoryItem.cantidad >= ingredient.cantidad : false
          };
        });
      });
    }
  }

  isFavorite(recipeId: string): boolean {
    return this.favorites.some(fav => fav.recipeId === recipeId);
  }

  // Nuevo método para editar receta
  editRecipe(recipeId: string) {
    if (this.userId) {
      this.firestore.collection('recetas').doc(recipeId).get().subscribe((doc) => {
        if (doc.exists) {
          const recipeData: any = doc.data();
          if (recipeData.creadorId === this.userId) {
            console.log('Navegar a editar receta: ', recipeId);
            // Lógica para navegar a la página de edición
          } else {
            console.error('No tienes permisos para editar esta receta');
          }
        }
      });
    }
  }
}
<ion-header>
   <ion-buttons slot="start">
     <ion-menu-button></ion-menu-button>
   <ion-title>Recetas Favoritas</ion-title>
   </ion-buttons>
</ion-header>

<ion-toolbar>

    <ion-segment [(ngModel)]="selectedCategory" (ionChange)="filterFavorites()">
      <ion-segment-button value="desayuno">
        <ion-label>Desayuno</ion-label>
      </ion-segment-button>
      <ion-segment-button value="almuerzo">
        <ion-label>Almuerzo</ion-label>
      </ion-segment-button>
      <ion-segment-button value="cena">
        <ion-label>Cena</ion-label>
      </ion-segment-button>
      <ion-segment-button value="postre">
        <ion-label>Postre</ion-label>
      </ion-segment-button>
    </ion-segment>

  </ion-toolbar>

<!-- {{ filteredFavorites | json }} -->
<ion-content>
  <ion-list>
    <!-- Iterar sobre las recetas favoritas filtradas -->
    <ion-card *ngFor="let recipe of filteredFavorites">
      <ion-img *ngIf="recipe.imageUrl" [src]="recipe.imageUrl" alt="Imagen de la receta"></ion-img>

      <ion-card-header>
        <ion-card-title>{{ recipe.titulo }}</ion-card-title>
      </ion-card-header>

      <ion-card-content>
        <!-- {{ recipe  | json}} -->
        <!-- Mostrar una descripción corta -->
        {{ recipe.descripcion | slice: 0:100 }}...

        <!-- Botones para ver más y eliminar de favoritos -->
        <ion-button fill="clear" (click)="openRecipeModal(recipe)">Ver más</ion-button>
        <ion-button fill="clear" color="danger" (click)="removeFromFavorites(recipe.id)">
          <ion-icon 
          [name]="isFavorite(recipe.id) ? 'heart' : 'heart-outline'" 
          [color]="isFavorite(recipe.id) ? 'danger' : 'medium'">
        </ion-icon>
      </ion-button>
      <button *ngIf="receta.creadorId === userId" (click)="editRecipe(receta.id)">
        Editar Receta
      </button>
      </ion-card-content>
    </ion-card>
  </ion-list>

  <!-- Modal de detalles de la receta -->
  <ion-modal [isOpen]="isModalOpen" (didDismiss)="closeModal()">
    <ng-template>
      <ion-header>
        <ion-toolbar>
          <ion-title>{{ selectedRecipe?.titulo }}</ion-title>
          <ion-buttons slot="end">
            <ion-button (click)="closeModal()">Cerrar</ion-button>
          </ion-buttons>
        </ion-toolbar>
      </ion-header>

      <ion-content>
        <ion-img *ngIf="selectedRecipe?.imageUrl" [src]="selectedRecipe?.imageUrl" alt="Imagen de la receta"></ion-img>

        <ion-list>
          <ion-item>
            <ion-label>
              <h2>{{ selectedRecipe?.titulo }}</h2>
            </ion-label>
          </ion-item>

          <!-- Descripción completa -->

          <ion-item *ngFor="let ing of availableIngredients">
            <ion-label>
              {{ ing.nombre }} - {{ ing.cantidad }} {{ ing.unidad }}
              <span *ngIf="ing.disponible" style="color: green;">Disponible</span>
              <span *ngIf="!ing.disponible" style="color: red;">No disponible</span>
            </ion-label>
          </ion-item>


    <ion-item>
         <ion-label>
          <h2>{{ selectedRecipe?.descripcion }}</h2>
        </ion-label>
      </ion-item>


    </ion-list>

        <ion-button (click)="closeModal()">Cerrar</ion-button>
      </ion-content>
    </ng-template>
  </ion-modal>
</ion-content>
