import { Component, OnInit } from '@angular/core';
import { AngularFireStorage } from '@angular/fire/compat/storage';
import { finalize } from 'rxjs/operators';
import { RecipeService } from '../../services/recipe.service'; 
import { InventoryService } from '../../services/inventory.service'; 
import { AngularFireAuth } from '@angular/fire/compat/auth';

// Define la interfaz Ingredient
interface Ingredient {
  nombre: string;
  cantidad: number;
  unidad: string;
}

@Component({
  selector: 'app-add-recipe',
  templateUrl: './add-recipe.component.html',
  styleUrls: ['./add-recipe.component.scss']
})
export class AddRecipeComponent implements OnInit {
  titulo: string = '';
  imageUrl: string | null = null;
  descripcion: string = '';
  categoria: string = 'desayuno';
  ingredientes: Ingredient[] = []; // Cambiado de string[] a Ingredient[]
  ingrediente: string = '';
  cantidadIngrediente: number = 1;
  unidadIngrediente: string = 'unidad'; // Nueva propiedad
  selectedFile: File | null = null;
  imagePreview: string | ArrayBuffer | null = null;
  globalItems: any[] = [];

  constructor(
    private recipeService: RecipeService,
    private inventoryService: InventoryService,
    private auth: AngularFireAuth,
    private storage: AngularFireStorage
  ) {}

  ngOnInit() {
    this.loadGlobalItems();
  }

  loadGlobalItems() {
    this.inventoryService.getGlobalItems().subscribe(items => {
      this.globalItems = items;
    });
  }

  addIngredient() {
    if (this.ingrediente.trim()) {
      const nuevoIngrediente: Ingredient = {
        nombre: this.ingrediente.trim(),
        cantidad: this.cantidadIngrediente,
        unidad: this.unidadIngrediente
      };
      this.ingredientes.push(nuevoIngrediente);
      this.ingrediente = '';
      this.cantidadIngrediente = 1;
      this.unidadIngrediente = 'unidad';
    }
  }

  addRecipe() {
    if (this.selectedFile) {
      const filePath = `recipes/${new Date().getTime()}_${this.selectedFile.name}`;
      const fileRef = this.storage.ref(filePath);
      const uploadTask = this.storage.upload(filePath, this.selectedFile);

      uploadTask.snapshotChanges().pipe(
        finalize(() => {
          fileRef.getDownloadURL().subscribe(url => {
            this.imageUrl = url; 
            this.saveRecipe();
          });
        })
      ).subscribe(
        () => {},
        (error) => console.error('Error al subir la imagen:', error)
      );
    } else {
      this.saveRecipe(); 
    }
  }

  saveRecipe() {
    this.auth.user.subscribe(user => {
      if (user) {
        const recipe = {
          titulo: this.titulo,
          imageUrl: this.imageUrl,
          descripcion: this.descripcion,
          categoria: this.categoria,
          ingredientes: this.ingredientes, // Ahora es un array de objetos `Ingredient`
          creadorId: user.uid
        };
        this.recipeService.addRecipe(recipe).then(() => {

        }).catch(error => {
          console.error('Error al agregar la receta:', error);
        });
      }
    });
  }


  onFileSelected(event: Event) {
    const file = (event.target as HTMLInputElement).files?.[0];
    if (file) {
      this.selectedFile = file;

      const reader = new FileReader();
      reader.onload = () => {
        this.imagePreview = reader.result;
      };
      reader.readAsDataURL(file);
    }
  }
}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>Agregar Receta</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list>
    <!-- Título de la receta -->
    <ion-item>
      <ion-label position="stacked">Título</ion-label>
      <ion-input [(ngModel)]="titulo"></ion-input>
    </ion-item>

    <!-- Subir imagen -->
    <ion-item>
      <input type="file" (change)="onFileSelected($event)" accept="image/*">
    </ion-item>

    <!-- Vista previa de la imagen -->
    <ion-item *ngIf="imagePreview">
      <img [src]="imagePreview" alt="Vista previa de la imagen" style="width: 100%; max-height: 200px;">
    </ion-item>

    <!-- Descripción de la receta -->
    <ion-item>
      <ion-label position="stacked">Descripción</ion-label>
      <ion-textarea [(ngModel)]="descripcion"></ion-textarea>
    </ion-item>

    <!-- Categoría de la receta -->
    <ion-item>
      <ion-label>Categoría</ion-label>
      <ion-select [(ngModel)]="categoria">
        <ion-select-option value="desayuno">Desayuno</ion-select-option>
        <ion-select-option value="almuerzo">Almuerzo</ion-select-option>
        <ion-select-option value="cena">Cena</ion-select-option>
        <ion-select-option value="postre">Postre</ion-select-option>
      </ion-select>
    </ion-item>

    <!-- Seleccionar un ingrediente predeterminado -->
    <ion-item>
      <ion-label>Seleccionar Ingrediente</ion-label>
      <ion-select [(ngModel)]="ingrediente">
        <ion-select-option *ngFor="let item of globalItems" [value]="item.nombre">{{ item.nombre }}</ion-select-option>
      </ion-select>
    </ion-item>

    <!-- Campos para ingresar la cantidad y la unidad del ingrediente -->
    <ion-item>
      <ion-label position="stacked">Cantidad</ion-label>
      <ion-input type="number" [(ngModel)]="cantidadIngrediente"></ion-input>
    </ion-item>

    <ion-item>
      <ion-label>Unidad</ion-label>
      <ion-select [(ngModel)]="unidadIngrediente">
        <ion-select-option value="gramos">Gramos</ion-select-option>
        <ion-select-option value="ml">ml</ion-select-option>
        <ion-select-option value="unidad">Unidad</ion-select-option>
      </ion-select>
    </ion-item>

    <!-- Botón para añadir el ingrediente -->
    <ion-item>
      <ion-button (click)="addIngredient()">Añadir Ingrediente</ion-button>
    </ion-item>

    <!-- Lista de ingredientes añadidos -->
    <ion-list>
      <ion-item *ngFor="let ing of ingredientes">
        {{ ing.nombre }} - {{ ing.cantidad }} {{ ing.unidad }}
      </ion-item>
    </ion-list>

    <!-- Botón para guardar la receta -->
    <ion-button expand="full" (click)="addRecipe()">Agregar Receta</ion-button>
  </ion-list>
</ion-content>
